import React, { useRef, useEffect, useState, Fragment } from "react";

const Landing = () => {
  const refwolfverine = useRef(null);
  const [btn, setBtn] = useState(false);
  useEffect(()=>{
    refwolfverine.current.classList.add("startingImg");
    setTimeout(()=>{
        refwolfverine.current.classList.remove("startingImg");
        setBtn(true);
    }, 1500)
  }, [])

  const setLeftImg = () => { refwolfverine.current.classList.add("leftImg"); }

  const setRightImg = () => { refwolfverine.current.classList.add("leftImg"); }

const dispalyBtn = btn && (
    <Fragment>
    <div className="leftBox">
        <button onMouseOver={setLeftImg} className="btn-welcome">Inscription</button>
    </div>
    <div className="rightBox">
        <button onMouseOver={setrightImg} className="btn-welcome">Connexion</button>
    </div>
    </Fragment>
)

  return (
    <main ref={refwolfverine} className="welcomePage">
      {dispalyBtn}
    </main>
  );
};

export default Landing;
