import React, { useRef, useEffect, useState, Fragment } from "react";

const Landing = () => {
  const refwolfverine = useRef(null);
  const [btn, setBtn] = useState(false);
  useEffect(() => {
    refwolfverine.current.classList.add("startingImg");
    setTimeout(() => {
      refwolfverine.current.classList.remove("startingImg");
      setBtn(true);
    }, 1500);
  }, []);

  const setLeftImg = () => {
    refwolfverine.current.classList.add("leftImg");
  };
  const setRightImg = () => {
    refwolfverine.current.classList.add("rightImg");
  };
  const clearImg = () => {
    if (refwolfverine.current.classList.contains("lefttImg")) {
      refwolfverine.current.classList.remove("lefttImg");
    } else if (refwolfverine.current.classList.contains("rightImg")) {
      refwolfverine.current.classList.remove("rightImg");
    }
  };

  const dispalyBtn = btn && (
    <Fragment>
      <div className="leftBox" onMouseOver={setLeftImg} onMouseOut={clearImg}>
        <button className="btn-welcome">Inscription</button>
      </div>
      <div className="rightBox" onMouseOver={setRightImg} onMouseOut={clearImg}>
        <button className="btn-welcome">Connexion</button>
      </div>
    </Fragment>
  );

  return (
    <main ref={refwolfverine} className="welcomePage">
      {dispalyBtn}
    </main>
  );
};

export default Landing;
